import React, { useState } from "react";
import NavBar from "./NavBar";

const LostFound = () => {
  if (localStorage.getItem("active") == 1) {
    console.log("active");
    console.log(localStorage.getItem("name"));
    console.log(localStorage.getItem("email"));
    console.log(localStorage.getItem("photo"));
  } else {
    window.location.replace("/signIn");
  }
  const [time,setTime] = useState(0);
  const [result,setResult] = useState([]);
  const setitem = (data) => {
    console.log(data)
    setResult([
      {
        data: data,
      },
    ]);
    console.log(result);
  };
  async function getImages() {
    try {
      const response = await fetch(
        // "https://arcane-brushlands-01906.herokuapp.com/api/read",
        `http://localhost:3020/`
      );
      const data = await response.json();
      setitem(data.files);
      console.log(data.files[0].filename);
      //  const re = data.result;

      console.log(result[0].data);
    } catch (err) {
      console.log(err);
    }
  }
  if (time == 0) {
    getImages();
    setTime(1);
  }
  return (
    <div className="">
      <NavBar />
      {/* {result[0] && (
        <>
          {result[0].data[0].filename}
          {result[0].data.map((file) => (
            <>
              <img
                className="h-20"
                src={"http://localhost:3020/image/" + file.filename}
              ></img>
            </>
          ))}
        </>
      )} */}
      <div className="p-10">
        <h1 class="text-white text-2xl ml-15">FIND MY ITEM</h1>
        <form
          action="http://localhost:3020/upload"
          method="POST"
          enctype="multipart/form-data"
        >
          <div class=" flex">
            <input name="url" value={window.location.href} hidden></input>
            <input
              className="p-3 bg-black text-white rounded-sm border-2 border-gray-600 h-10 mt-10 w-1/3"
              name="item"
              value=""
              placeholder="Item name & description"
            ></input>

            <div className="flex mt-10 ">
              <label for="file" class="custom-file-label">
                Add Image
              </label>
              <input className="text-white" type="file" name="file" id="file" />
            </div>
          </div>
          <input
            type="submit"
            value="Submit"
            class="btn btn-primary btn-block"
          />
        </form>
      </div>
    </div>
  );
};

export default LostFound;
