import React, { useState } from "react";
import NavBar from "./NavBar";
import { Link } from "react-router-dom";

const Courses = () => {
  if (localStorage.getItem("active") == 1) {
    console.log("active");
    console.log(localStorage.getItem("name"));
    console.log(localStorage.getItem("email"));
    console.log(localStorage.getItem("photo"));

    const [time, setTime] = useState(0);
    const [result, setResult] = useState([]);
    const [search, setSearch] = useState("");
    const setitem = (data) => {
      setResult([
        {
          data: data,
        },
      ]);
    };
    async function getCourses() {
      try {
        const response = await fetch(
          // "https://arcane-brushlands-01906.herokuapp.com/api/read",
          "http://localhost:3020/api/courses/read"
        );
        const data = await response.json();
        console.log(data);
        setitem(data.result);
        console.log(result[0].data);
        const re = data.result;

        // console.log(typeof(re));
      } catch (err) {
        console.log(err);
      }
    }
    if (time == 0) {
      getCourses();
      setTime(1);
    }

    return (
      <div className="">
        <NavBar />
        <div className="p-20 text-white  ">
          <input
            className="p-2 pl-3  w-1/2 rounded-lg border-2 bg-white text-black border-slate-500"
            type="search"
            name="search"
            placeholder="Search your Course"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          <button
            onClick={getCourses}
            className="bg-yell ml-4 text-black p-2  pl-3 pr-3  rounded-md "
          >
            Search
          </button>
          <h1 className="text-4xl font-semibold mt-10">Computer Science</h1>
          <div className="flex flex-wrap">
            {result.length != 0 && (
              <>
                {result[0].data.map((value) => (
                  <>
                    {value.courseCode[0] == "C" && value.courseCode[1] == "S" && (
                      <>
                        {(value.courseName
                          .toLocaleLowerCase()
                          .replace(/\s/g, "")
                          .match(search.toLocaleLowerCase()) != null ||
