import React, { useState } from "react";
import NavBar from "./NavBar";

const Alumni = () => {
  const [alumniName,setAlumniName] = useState("")
  const [placedAt,setPlacedAt] = useState("")
  const [about,setAbout] = useState("")
  const [blog,setBlog] = useState("")
  const [url,setUrl] = useState("")
  const [time, setTime] = useState(0);
      const [result, setResult] = useState([]);
      async function AddBlog(event) {
        event.preventDefault();
        try {
          const response = await fetch(
            //    "https://arcane-brushlands-01906.herokuapp.com/api/register",
            "http://localhost:3020/api/alumni/blog/add",
            {
              method: "POST",
              headers: {
                "Content-Type": "application/json",
              },
              body: JSON.stringify({
                alumniName: localStorage.getItem("name"),
                message: req.body.blog,
                date: dt,
                photo: req.body.photo,
              }),
            }
          );
          const data = await response.json();
          //  console.log(data.result[data.result.length - 1]);

          setBlog("");
        } catch (err) {
          console.log(err);
        }
      }
  const setitem = (data) => {
    setResult([
      {
        data: data,
      },
    ]);
  };
   async function getAlumniDetail() {
     try {
       const response = await fetch(
         // "https://arcane-brushlands-01906.herokuapp.com/api/read",
         `http://localhost:3020/api/alumni/read`
       );
       const data = await response.json();
       setitem(data.result);
       console.log(data.result);
       //  const re = data.result;

       console.log(result[0].data);
     } catch (err) {
       console.log(err);
     }
   }
   if (time == 0) {
     getAlumniDetail();
     setTime(1);
   }
  return (
    <div className="">
      <NavBar />

      <body>
        <div className="imageContainer">
          <img src="alumni.png" />

          <div className="caption center">IIT BHILAI JEMS</div>
        </div>

        <div className="container">
          <div className="team">
            {result.length != 0 && (
              <>
                {result[0].data.map((alumni) => (
                  <>
                    <div className="member">
                      <img
                        src={"http://localhost:3020/image/" + alumni.photo}
                      ></img>
                      <h3 style={{ color: "#fff" }}>{alumni.alumniName}</h3>
                      <span>{alumni.placedAt}</span>
                      <p>{alumni.about}</p>

                      <a
                        className="text-black bg-white p-3 rounded-md"
                        href={alumni.linkedInUrl}
                      >
                        Connect
                      </a>
                    </div>
                  </>
                ))}
              </>
            )}
          </div>
        </div>
        <div className="flex mt-10 ">
          <form onSubmit={AddBlog}>
            <input
              placeholder="Add blogs"
              className="w-72 border-2 h-20 border-zinc-400  p-3 bg-black rounded-lg"
              value={blog}
              onChange={(e) => setBlog(e.target.value)}
              type="text"
              required
            ></input>
            <button
              type="submit"
              className="bg-yell mt-6 p-3 pl-4 pr-4 text-black sm:ml-10 rounded-full "
            >
              POST
            </button>
          </form>
        </div>
        {localStorage.getItem("email") == "anshukumar@iitbhilai.ac.in" && (
          <>
            <div className="p-10 ">
              <h1 class="text-white text-2xl ml-15">
                Add Alumni
                <p className="text-sm">
                  (*You must have authentication for adding new alumni)
                </p>
              </h1>
              <form
                action="http://localhost:3020/upload/alumni"
                method="POST"
                enctype="multipart/form-data"
              >
                <div class=" ">
                  <input name="url" value={window.location.href} hidden></input>
                  <input
                    className="p-3 bg-black text-white rounded-md border-2 border-gray-600 h-12 mt-3 sm:mt-10 w-3/4 sm:w-1/3"
                    name="alumniName"
                    value={alumniName}
                    onChange={(e) => setAlumniName(e.target.value)}
                    type="text"
                    placeholder="Name"
                    required
                  ></input>

                  <input
                    className="p-3 bg-black text-white rounded-md border-2 border-gray-600 h-12 sm:mt-10 mt-3 w-3/4 sm:w-1/3 sm:ml-4"
                    name="placedAt"
                    value={placedAt}
                    onChange={(e) => setPlacedAt(e.target.value)}
                    type="text"
                    placeholder="Placed At"
                    required
                  ></input>
                  <br></br>
                  <input
                    className="p-3 bg-black text-white rounded-md border-2 border-gray-600 h-12 sm:mt-10 mt-3 w-3/4 sm:w-1/3 "
                    name="about"
                    value={about}
                    onChange={(e) => setAbout(e.target.value)}
                    type="text"
                    placeholder="About"
                    required
                  ></input>
                  <input
                    className="p-3 bg-black text-white rounded-md border-2 border-gray-600 h-12 sm:mt-10 mt-3 w-3/4 sm:w-1/3 sm:ml-4"
                    name="linkedInUrl"
                    value={url}
                    onChange={(e) => setUrl(e.target.value)}
                    type="text"
                    placeholder="LinkedIn URL"
                    required
                  ></input>

                  <div className=" mt-7 sm:ml-5 ">
                    <label for="file" className="text-white text-xl  ml-3">
                      Upload Image
                    </label>
                    <br></br>
                    <input
                      className="text-white sm:ml-3 mt-2"
                      type="file"
                      name="file"
                      id="file"
                      required
                    />
                  </div>
                </div>
                <button
                  className="bg-yell mt-6 text-lg flex  text-black pl-3 pr-3 p-1 rounded-xl"
                  type="submit"
                >
                  ADD
                </button>
              </form>
            </div>
          </>
        )}
        {localStorage.getItem("email") != "anshukumar@iitbhilai.ac.in" && (
          <>
            <div className="p-10 opacity-20 ">
              <h1 class="text-white text-2xl ml-15">
                Add Alumni
                <p className="text-sm">
                  (*You must have authentication for adding new alumni)
                </p>
              </h1>
              <form
                action="http://localhost:3020/upload/alumni"
                method="POST"
                enctype="multipart/form-data"
              >
                <div class=" ">
                  <input name="url" value={window.location.href} hidden></input>
                  <input
                    className="p-3 bg-black text-white rounded-md border-2 border-gray-600 h-12 mt-3 sm:mt-10 w-3/4 sm:w-1/3"
                    name="alumniName"
                    value={alumniName}
                    onChange={(e) => setAlumniName(e.target.value)}
                    type="text"
                    placeholder="Name"
                    required
                    disabled
                  ></input>

                  <input
                    className="p-3 bg-black text-white rounded-md border-2 border-gray-600 h-12 sm:mt-10 mt-3 w-3/4 sm:w-1/3 sm:ml-4"
                    name="placedAt"
                    value={placedAt}
                    onChange={(e) => setPlacedAt(e.target.value)}
                    type="text"
                    placeholder="Placed At"
                    required
                    disabled
                  ></input>
                  <br></br>
                  <input
                    className="p-3 bg-black text-white rounded-md border-2 border-gray-600 h-12 sm:mt-10 mt-3 w-3/4 sm:w-1/3 "
                    name="about"
                    value={about}
                    onChange={(e) => setAbout(e.target.value)}
                    type="text"
                    placeholder="About"
                    required
                    disabled
                  ></input>
                  <input
                    className="p-3 bg-black text-white rounded-md border-2 border-gray-600 h-12 sm:mt-10 mt-3 w-3/4 sm:w-1/3 sm:ml-4"
                    name="linkedInUrl"
                    value={url}
                    onChange={(e) => setUrl(e.target.value)}
                    type="text"
                    placeholder="LinkedIn URL"
                    required
                    disabled
                  ></input>

                  <div className=" mt-7 sm:ml-5 ">
                    <label for="file" className="text-white text-xl  ml-3">
                      Upload Image
                    </label>
                    <br></br>
                    <input
                      className="text-white sm:ml-3 mt-2"
                      type="file"
                      name="file"
                      id="file"
                      required
                      disabled
                    />
                  </div>
                </div>
                <button
                  className="bg-yell mt-6 text-lg flex  text-black pl-3 pr-3 p-1 rounded-xl"
                  type="submit"
                  disabled
                >
                  ADD
                </button>
              </form>
            </div>
          </>
        )}
      </body>
    </div>
  );
};

export default Alumni;
